import React, { Component } from "react";
import { GetLocalIcon, GetGoogleIcon } from "./icons";
import { getRecorddata, createRecord, bulkcreateRecord } from "../db/index";
import Calendernewaction from "./calendernewaction";
import Calenderviewaction from "./calenderviewaction";
import {
  getMonthStringDisplay,
  getMonthDigitalDisplay,
  getDateDigitalDisplay,
  getDayStringDisplay,
  getUniqueArray,
} from "../js/index";

class F extends Component {
  constructor(props) {
    super(props);
    let d = new Date();
    let month = (d.getMonth() + 1).toString();
    if (month.length == 1) {
      month = "0" + month;
    }
    let date = d.getDate().toString();
    if (date.length == 1) {
      date = "0" + date;
    }

    let year = d.getFullYear();

    this.state = {
      compType: "list",
      viewMeetingData: "",
      viewtype: "day",
      currentdate: date,
      currentmonth: month,
      currentyear: year,
      activedate: date,
      activemonth: month,
      activeyear: year,
      eventdata: [],
      viewStartDate: "",
      viewEndDate: "",
      viewTypeMonthRetrievalStartDate1: "",
      viewTypeMonthRetrievalStartDate2: "",
      viewTypeWeekRetrievalStartDate1: "",
      viewTypeWeekRetrievalStartDate2: "",
      defaultNewMeetingDate: "",
      defaultNewMeetingHour: "",
    };
  }

  async componentDidMount() {
    console.log(this.props);
    let {
      orgname,
      userprofileid,
      userid,
      tablename,
      datadisplaytype,
      viewname,
      evtdefaultsortparam,
      hidesidebar,
      defaultdatafilter,
    } = this.props.compprops;

    let { activedate, activemonth, activeyear, viewtype } = this.state;
    if (viewname == null) {
      viewname = "recentlyviewed";
    }
    this.getRecordListMetadataAndDatafromServer({
      orgname: orgname,
      userProfileId: userprofileid,
      userId: userid,
      tableName: tablename,
      datasortparambeginswith: "",
      datasortparamequalsto: "",
      action: "",
      // viewName: viewname,
      datadisplaytype: datadisplaytype,
      evtdefaultsortparam: evtdefaultsortparam,
      targetdate: activedate,
      targetmonth: activemonth,
      targetyear: activeyear,
      viewtype: viewtype,
      hidesidebar: hidesidebar,
      defaultdatafilter: defaultdatafilter,
    });
  }

  async getRecordListMetadataAndDatafromServer(props) {
    let {
      orgname,
      userProfileId,
      userId,
      tableName,
      datasortparambeginswith,
      datasortparamequalsto,
      action,
      viewtype,
      datadisplaytype,
      evtdefaultsortparam,
      targetdate,
      targetmonth,
      targetyear,
      istoday,
      viewStartDate,
      viewEndDate,
      viewTypeMonthRetrievalStartDate1,
      viewTypeMonthRetrievalStartDate2,
      viewTypeWeekRetrievalStartDate1,
      viewTypeWeekRetrievalStartDate2,
      hidesidebar,
      defaultdatafilter,
    } = props;
    console.log(props);
    let { mainPanelHtmlObj } = this.state;

    if (viewtype == "day") {
      let month, date, year, usrsortparamToday;

      date = targetdate;
      month = targetmonth;
      year = targetyear;
      usrsortparamToday = evtdefaultsortparam + "-" + userId + "-" + year + "-";

      if (month != "" && month != undefined) {
        usrsortparamToday = usrsortparamToday + month + "-";
      }
      if (date != "" && date != undefined) {
        usrsortparamToday = usrsortparamToday + date;
      }

      let dataParams = {
        objectName: tableName,
        objectData: {},
        keyConditions: [
          {
            field: "orgname",
            value: orgname,
            expression: "=",
          },
          { field: "id", value: usrsortparamToday, expression: "beginswith" },
        ],
        filterConditions: [],
        pageSize: "",
        limit: "",
        exclusiveStartKey: "",
      };
      let dataresult = await getRecorddata(dataParams);
      if (dataresult.isSuccess === "false") {
        alert("data" + dataresult.message);
      } else {
