import React, { Component } from "react";

class F extends Component {
  constructor(props) {
    super(props);
    this.state = { subject: "", body: "" };
  }

  sendmail = () => {
    let { to } = this.props.compprops;
    let { subject, body } = this.state;

    window.open(
      `https://mail.google.com/mail/u/0/?fs=1&to=${to}&su=${subject}&body=${body}&tf=cm`
    );
  };

  handlechange = (e) => {
    let { templatebody } = this.props.compprops;
    let replacedText = templatebody.replace("templatebodytext", e.target.value);
  };

  render() {
    return (
      <div className="org-cdivv-esp">
        <div>
          <div>
            <b>Course</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <button onClick={this.sendmail}>Submit</button>
        </div>
      </div>
    );
  }
}

export default F;
