import React, { Component } from "react";
import Logo from "../images/snowmount.jpg";
import "../App.css";

class F extends Component {
  constructor(props) {
    super(props);
    this.state = { activePageName: "" };
  }

  tabClick = (x) => {
    this.setState({ activePageName: x });
