import React, { Component } from "react";

class F extends Component {
  constructor(props) {
    super(props);
    this.state = { subject: "", body: "", fromemail: "", fromphoneno: "" };
  }

  sendmail = () => {
    let { toemail, templatesubject, templatebody } = this.props.compprops;
    let { fromname, fromemail, fromphoneno, course } = this.state;
    if (fromname == "" || fromemail == "" || course == "") {
      alert("plese enter required details");
    } else {
      let subject = templatesubject.replace("templatefromnametext", fromname);
      subject = templatesubject.replace("templatefromemailtext", fromemail);
      subject = templatesubject.replace("templatefromphonenotext", fromphoneno);
      subject = templatesubject.replace("templatecoursetext", course);
      let body = templatebody.replace("templatefromnametext", fromname);
      body = templatebody.replace("templatefromemailtext", fromemail);
      body = templatebody.replace("templatefromphonenotext", fromphoneno);
      body = templatebody.replace("templatecoursetext", course);

      window.open(
        `https://mail.google.com/mail/u/0/?fs=1&to=${toemail}&su=${subject}&body=${body}&tf=cm`
      );
    }
  };

  handlechange = (e) => {
    let name = e.target.name;
    this.setState({ name: e.target.value });
  };

  render() {
    return (
      <div className="org-cdivv-esp">
        <div>
          <div>
            <b>Name</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Email</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Course</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Contact No</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <button onClick={this.sendmail}>Submit</button>
        </div>
      </div>
    );
  }
}

export default F;
