import React, { Component } from "react";

class F extends Component {
  constructor(props) {
    super(props);
    this.state = { subject: "", body: "" };
  }

  sendmail = () => {
    let { to } = this.props.compprops;
    let { subject, body, name, from } = this.state;

    window.open(
      `https://mail.google.com/mail/u/0/?fs=1&to=${to}&su=${subject}&body=${body}&tf=cm`
    );
  };

  handlechange = (e) => {
    let { templatesubject, templatebody } = this.props.compprops;
    if (e.target.name === "from") {
      this.setState({ from: e.target.value });
    } else if (e.target.name === "name") {
      this.setState({ name: e.target.value });
    } else if (e.target.name === "name") {
      this.setState({ name: e.target.value });
    }
    let replacedSubject = templatesubject.replace(
      "templatesubjecttext",
      e.target.value
    );
    let replacedBody = templatebody.replace("templatebodytext", e.target.value);
    this.setState({ subject: replacedSubject, body: replacedBody });
  };

  render() {
    return (
      <div className="org-cdivv-esp">
        <div>
          <div>
            <b>Name</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Email</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Course</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <div>
            <b>Contact No</b>
          </div>
          <div>
            <input style={{ width: "50%" }} onChange={this.handlechange} />
          </div>
        </div>

        <div>
          <button onClick={this.sendmail}>Submit</button>
        </div>
      </div>
    );
  }
}

export default F;
